#!/usr/bin/env kscript

package nl.paulienvanalst.adventOfCode.twentytwentytwo.Dec


val input = ""

println("Solution to part 1:")
val markerRequirement1 = 4
val skippedBeforeFourUnique1 = input.toCharArray()
        .mapIndexed { index, _ -> findMarker(index, markerRequirement1) }
        .first { it.second == markerRequirement1 }

println("Found ${skippedBeforeFourUnique1.first}")


println("Solution to part 2:")
val markerRequirement2 = 14
val skippedBeforeFourUnique2 = input.toCharArray()
    .mapIndexed { index, _ -> findMarker(index, markerRequirement2) }
    .first { it.second == markerRequirement2 }

println("Found ${skippedBeforeFourUnique2.first}")

fun findMarker(index: Int, markerRequirement: Int) =
    if (index > markerRequirement && input.subSequence(index - markerRequirement + 1, index - 1).toSet().size < markerRequirement) {
        index to input.subSequence(index - markerRequirement, index).toSet().size
    } else index to 0
